import React from "react";
import "./Faucet.css";
import { ethers } from "ethers";
import { useState } from "react";
import { dotABI, dotAddress } from "./dotContract/constants";
import { Redirect } from "react-router-dom";
export default function Faucet() {
  const { ethereum } = window;
  const [transferHash, setTransferHash] = useState(null);
  const [home, setHome] = useState(false);
  const [currentAccount, setCurrentAccount] = useState("");
  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const dotContract = new ethers.Contract(dotAddress, dotABI, signer);
    return dotContract;
  };

  const dotContract = createEthereumContract();

  const transferTokens = async (e) => {
    e.preventDefault();
    let transferAmount = e.target.tokens.value;
    const parsedAmount = ethers.utils.parseEther(transferAmount);
    let receieverAddress = e.target.address.value;
    let txt = await dotContract.Mint(receieverAddress, parsedAmount);
    setTransferHash(txt.hash);
  };

  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });
  async function connectWallet() {
    if (typeof ethereum !== "undefined") {
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      setCurrentAccount(accounts[0]);
    }
  }
  connectWallet();

  function handleBack() {
    setHome(true);
  }
  return (
    <div className="faucet">
      <button className="one-more button-su" onClick={handleBack}>
        {/* <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          fillRule="currentColor"
          className="bi bi-arrow-left"
          viewBox="0 0 16 16"
        >
          <path
            fillRule="evenodd"
            d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
          />
        </svg> */}
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          fill="currentColor"
          class="bi bi-house"
          viewBox="0 0 16 16"
        >
          <path
            fill-rule="evenodd"
            d="M2 13.5V7h1v6.5a.5.5 0 0 0 .5.5h9a.5.5 0 0 0 .5-.5V7h1v6.5a1.5 1.5 0 0 1-1.5 1.5h-9A1.5 1.5 0 0 1 2 13.5zm11-11V6l-2-2V2.5a.5.5 0 0 1 .5-.5h1a.5.5 0 0 1 .5.5z"
          />
          <path
            fill-rule="evenodd"
            d="M7.293 1.5a1 1 0 0 1 1.414 0l6.647 6.646a.5.5 0 0 1-.708.708L8 2.207 1.354 8.854a.5.5 0 1 1-.708-.708L7.293 1.5z"
          />
        </svg>
      </button>
      <form onSubmit={transferTokens}>
        <h1 className="heading-faucet">Donation Token Faucet</h1>
        <div className="input">
          <input
            id="address"
            type="text"
            required
            // placeholder="YOUR METAMASK WALLET ADDRESS"
            value={currentAccount}
            onChange={() => {}}
            autoComplete="off"
            className="form-input-faucet"
          />
        </div>
        <div className="input">
          <input
            id="tokens"
            type="number"
            min={0.000000000000000001}
            step={0.000000000000000001}
            required
            placeholder="NUMBER OF DOT TOKENS"
            autoComplete="off"
            className="form-input-faucet"
          />
        </div>
        <button className="button-64" type="submit">
          <span className="text">TRANSFER</span>
        </button>
      </form>
      {home && <Redirect to="/homePage" />}
    </div>
  );
}
