import React from "react";
import { ethers } from "ethers";
import { useState } from "react";
import { dotABI, dotAddress } from "./dotContract/constants";
export default function Faucet() {
  const { ethereum } = window;
  const [transferHash, setTransferHash] = useState(null);
  const [currentAccount, setCurrentAccount] = useState("");
  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const dotContract = new ethers.Contract(dotAddress, dotABI, signer);
    return dotContract;
  };

  const dotContract = createEthereumContract();

  const transferTokens = async (e) => {
    e.preventDefault();
    let transferAmount = e.target.tokens.value;
    const parsedAmount = ethers.utils.parseEther(transferAmount);
    let receieverAddress = e.target.address.value;
    let txt = await dotContract.Mint(receieverAddress, parsedAmount);
    setTransferHash(txt.hash);
  };

  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });
  async function connectWallet() {
    if (typeof ethereum !== "undefined") {
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      setCurrentAccount(accounts[0]);
    }
  }
  connectWallet();
  return (
    <div className="faucet">
      <form onSubmit={transferTokens}>
        <h1 className="heading">Donation Token Faucet</h1>
        <div className="input">
          <input
            id="address"
            type="text"
            required
            // placeholder="YOUR METAMASK WALLET ADDRESS"
            value={currentAccount}
            onChange={() => {}}
            autoComplete="off"
            className="form-input"
          />
        </div>
        <div className="input">
          <input
            id="tokens"
            type="number"
            min={0.000000000000000001}
            step={0.000000000000000001}
            required
            placeholder="NUMBER OF DOT TOKENS"
            autoComplete="off"
            className="form-input"
          />
        </div>
        <button className="button-64" type="submit">
          <span className="text">TRANSFER KAR DO PLIJ</span>
        </button>
      </form>
    </div>
  );
}
